<?phpif(!function_exists('doors_team_scode')){	function doors_team_scode($atts) {		$show_description = $columns = $itemperpage = $team_collapse = "";		extract( shortcode_atts( array(			'team_style' => 'style_1',			'columns'     => 4,			'itemperpage' => '10',			'layout'      => 1,   // 1: masonry  2: grid  3: large			'header' => 16,			'css_animation' => 'no',			'show_description' => 'yes',			'team_collapse' => 'yes'		), $atts ) );		$animation_classes =  "";		$data_animated = "";		if(($css_animation != 'no')){			$animation_classes =  " animated ";			$data_animated = "data-animated=$css_animation";		}		ob_start(); ?>		<?php if( $team_style == "style_2" ) { ?>      <div class="team-list-style2  small-up large-up-<?php echo esc_attr($columns); ?> <?php if($team_collapse == "") echo "collapse"; ?>">				<?php $loop = new WP_Query(array('post_type' => 'team-member', 'posts_per_page' => $itemperpage,));				while ($loop->have_posts()) : $loop->the_post(); ?>          <div            class="<?php echo esc_attr($animation_classes); ?> column column-block" <?php echo esc_attr($data_animated); ?>>            <div class="team-member">              <div class="team-member__picture">								<?php								$image_url = get_post_meta(get_the_ID(), 'pciture', true);								$image_url=image_from_url_relatives($image_url);								$image_id = doors_get_image_id($image_url);					if ( $image_id != '' ) { ?>						<?php 	print wp_get_attachment_image( $image_id, 'doors_team' );  ?>					<?php }					else { ?>							<?php 	the_post_thumbnail('wd_team') ; } ?>              </div>              <div class="team-member__info">                <h5 class="team-member__name"><span><?php the_title(); ?></span></h5>								<?php if (get_post_meta(get_the_ID(), 'job_title', true) != '') { ?>                  <p class="team-member__job"><span><?php echo get_post_meta(get_the_ID(), 'job_title', true); ?></span>                  </p>								<?php } ?>								<?php if ($team_style != 'style_1') { ?>                  <p class="team-member__desc"><?php echo get_the_excerpt() ?></p>								<?php } ?>              </div>							<?php							if ($team_style != 'style_1') {								$facebook = get_post_meta(get_the_ID(), 'doors_facebook', true);								$instagram = get_post_meta(get_the_ID(), 'doors_facebook', true);								$twitter = get_post_meta(get_the_ID(), 'doors_facebook', true);								if ($facebook || $instagram || $twitter) {									?>                  <div class="team-member__socialmedia">                    <ul>											<?php if ($facebook) { ?>                        <li><a href="<?php echo $facebook; ?>"><i class="fa fa-facebook"></i></a></li>											<?php }											if ($instagram) { ?>                        <li><a href="<?php echo $instagram; ?>"><i class="fa fa-instagram"></i></a></li>											<?php }											if ($twitter) { ?>                        <li><a href="<?php echo $twitter; ?>"><i class="fa fa-twitter"></i></a></li>											<?php } ?>                    </ul>                  </div>								<?php }							} ?>            </div>          </div>				<?php endwhile; ?>      </div>		<?php }		else{  ?>		<ul class="team-list small-up large-up-<?php echo esc_attr($columns); ?>        medium-up-<?php echo esc_attr($columns); ?> text-center <?php if($team_collapse == "") echo "collapse"; ?>">			<?php $loop = new WP_Query( array( 'post_type' => 'team-member', 'posts_per_page' => $itemperpage, ) );			while ( $loop->have_posts() ) : $loop->the_post();  ?>				<li class="<?php echo esc_attr($animation_classes); ?> column column-block" <?php echo esc_attr($data_animated); ?>>					<div class="team-member-item">						<div class="team-member-picture">							<?php							$image_url = get_post_meta(get_the_ID(), 'pciture', true);							$image_url=image_from_url_relatives($image_url);							$image_id = doors_get_image_id($image_url);							if ( $image_id != '' ) { ?>								<?php 	print wp_get_attachment_image( $image_id, 'doors_team' );  ?>							<?php }							else { ?>								<?php 	the_post_thumbnail('wd_team') ; } ?>						</div>						<div class="team-member-name-job-title">							<h3 class="team-member-name"><?php the_title(); ?></h3>							<?php if(get_post_meta(get_the_ID(), 'job_title', true) != '') { ?>								<h4><?php echo get_post_meta(get_the_ID(), 'job_title', true); ?></h4>							<?php } ?>						</div>						<?php if ( $show_description == "yes" && get_post_meta(get_the_ID(), 'description', true) != ""){ ?>							<div class="team-member-desc text-left">								<p><?php echo get_post_meta(get_the_ID(), 'description', true); ?></p>							</div>						<?php } ?>						<div class="team-membre-social-icons">							<ul>								<?php if (get_post_meta(get_the_ID(), 'doors_twitter', true) != ""): ?>									<li><a href="<?php echo get_post_meta(get_the_ID(), 'doors_twitter', true); ?>"><i class="fa fa-twitter"></i></a></li>								<?php endif ?>								<?php if (get_post_meta(get_the_ID(), 'doors_facebook', true) != ""): ?>									<li><a href="<?php echo get_post_meta(get_the_ID(), 'doors_facebook', true); ?>"><i class="fa fa-facebook"></i></a></li>								<?php endif ?>								<?php if (get_post_meta(get_the_ID(), 'doors_linkedin', true) != ""): ?>									<li><a href="<?php echo get_post_meta(get_the_ID(), 'doors_linkedin', true); ?>"><i class="fa fa-linkedin"></i></a></li>								<?php endif ?>							</ul>						</div>					</div>				</li>			<?php endwhile; ?>		</ul><?php }?>		<?php		return ob_get_clean();	}	add_shortcode( 'doors_team', 'doors_team_scode' );}